<?phpclass Upload {    private $files;    function __construct($files) {        $this->files = $files;    }    public function uploadFile() {        $array = array();        foreach ($this->files as $key => $value) {            if ($value['name'] != '') {                $file = $value['name'];                $prefijo = substr(md5(uniqid(rand())), 0, 10);                $destino = realpath(dirname(__FILE__)) . "/" . time() . "_" . $prefijo . "_" . $file;                array_push($array, $destino);                if (!copy($value['tmp_name'], $destino)) {                    return false;                }            }        }        return $array;    }}?>